var app = new Vue({
  el: "#app",
  data: {
    bpi: null,
    hasError: false,
    loading: true
  },
  mounted: function () {
    axios.get('https://api.coindesk.com/v1/bpi/currentprice.json')
      .then(function (response) {
        // console.log(response.data.bpi)
        // ビットコイン価格を取得してプロパティにデータを格納
        this.bpi = response.data.bpi
      }.bind(this))
      .catch(function (error) {
        console.log(error)
        this.hasError = true
      }.bind(this))
      // fainallyは通信に関する全ての処理が終わった時に呼ばれる
      .finally(function () {
        this.loading = false
      }.bind(this))
  },
  filters: {
    currencyDecimal(value) {
      return value.toFixed(2)
    }
  }
})
